-- HelpInMinutes Skill Taxonomy Seeding Script
-- This script seeds the database with the complete skill taxonomy
-- Categories: 15 main categories with 100+ micro-tasks

-- Create ENUM types if not exist
DO $$ BEGIN
    CREATE TYPE task_category_key AS ENUM (
        'HOME_REPAIR', 'ELECTRICAL', 'PLUMBING', 'CLEANING', 
        'PACKING_MOVING', 'DELIVERY', 'TECHNICAL_SUPPORT', 
        'TUTORING', 'PET_CARE', 'GROCERY_SHOPPING', 'CAR_WASH',
        'APPLIANCE_REPAIR', 'LOCKSMITH', 'PEST_CONTROL', 'OTHER'
    );
EXCEPTION
    WHEN duplicate_object THEN null;
END $$;

-- Create ENUM for skill status
DO $$ BEGIN
    CREATE TYPE skill_status AS ENUM ('ACTIVE', 'DEPRECATED', 'HIDDEN');
EXCEPTION
    WHEN duplicate_object THEN null;
END $$;

