-- Create indexes for better query performance
CREATE INDEX IF NOT EXISTS idx_skills_category ON skills(category_id);
CREATE INDEX IF NOT EXISTS idx_skills_name ON skills(name_en);
CREATE INDEX IF NOT EXISTS idx_categories_name ON categories(name_en);
CREATE INDEX IF NOT EXISTS idx_skills_status ON skills(status);

-- Add comments to explain the data model
COMMENT ON TABLE categories IS 'Main task categories for HelpInMinutes platform';
COMMENT ON TABLE skills IS 'Micro-tasks within each category that helpers can perform';
COMMENT ON COLUMN skills.skill_type IS 'STANDARD: Regular task, EXPRESS: Urgent task with premium pricing, PREMIUM: Specialized skill requiring expertise';
